import Questions from "./Questions";
import { useState, useEffect } from "react";
const Questionaire = () => {
    const [quesNum, setQuesNum] =  useState(0);
    const [answers, setAnswers] = useState([]);
    const [questions, setQuestions] = useState([]);

    const fetchQues = async (taskText) => {
        try {
        const response = await fetch(
            'https://react-app-38291-default-rtdb.firebaseio.com/questions.json'
        );

        if (!response.ok) {
            throw new Error('Request failed!');
        }

        const data = await response.json();

        const loadedQuestions = [];

        for (const quesKey in data) {
            loadedQuestions.push({ id: quesKey, text: data[quesKey] });
        }

        setQuestions(loadedQuestions);
        } catch (err) {
          //setError(err.message || 'Something went wrong!');
        }
    };
    const fetchQuestions = [
        {
            "qno": 0,
            "question": "Liquid in based on which technology?",
            "options": [
                "React", "Ruby", "Java", "Pyhton"
            ],
            "answer": "Ruby"
        },
        {
            "qno": 1,
            "question": "What is the role files containing in 'Locale' folder?",
            "options": [
                "Rendering html in themes", "Translating content in themes", "Contains theme data", "None of these"
            ],
            "answer": "Ruby"
        }
    ];

    useEffect(() => {
        fetchQues();
    }, []);
    console.log(questions);
    const questionsHandler = (num) => {
        setQuesNum(num);
    }
    const answersHandler = (ans) => {
        setAnswers([ans, ...answers]);
    }
    console.log(answers);
    return(
        <Questions questions={fetchQuestions} setQuesNum={questionsHandler} quesNum={quesNum} setAns={answersHandler} ans={answers}/>
    );
}

export default Questionaire;
