import React, { useEffect } from "react";
import { useState } from "react/cjs/react.development";
import AddQuestionForm from "./AddQuestionForm";

const AdminDashboard = () => {
    const [enableForm, setEnableForm] = useState(true);
    const [error, setError] = useState(false);
    const [success, setSuccess] = useState(false);

    const search = window.location.search;
    useEffect(() => {
        if(search.includes("danish") || search.includes("shubham")){
            setEnableForm(true);
        }
    }, [search]);

    const addQuestionHandler = async (data) => {
        try {
            const response = await fetch(
              'https://react-app-38291-default-rtdb.firebaseio.com/questions.json',
              {
                method: 'POST',
                body: JSON.stringify({ data }),
                headers: {
                  'Content-Type': 'application/json',
                },
              }
            );

            if (!response.ok) {
              throw new Error('Request failed!');
            }

            const res = await response.json();
            setSuccess(true);
            // const generatedId = data.name; // firebase-specific => "name" contains generated id
            // const createdTask = { id: generatedId, text: taskText };

            // props.onAddTask(createdTask);
        } catch (err) {
            setError(err.message || 'Something went wrong!');
        }
    }
    const isError = error && "error";

    return(
            <React.Fragment>
                <h2>Hello Admin</h2>
                {enableForm ? <AddQuestionForm addQuestion={addQuestionHandler} errorMsg={isError}/> : ""}
            </React.Fragment>
        );
}

export default AdminDashboard;
