import QuesItem from "./QuesItem";
import SectionMedium from '../UI/SectionMedium';
import classes from '../UI/Card.module.css';

const Questions = (props) => {
    const questions = props.questions;
    const currentQues = props.quesNum;
    const question = questions[currentQues];

    const getSelectedOption = (value) => {
        console.log(value);
    }

    const content = <QuesItem key={currentQues} ques={question} index={currentQues} option={getSelectedOption}/>;
    const formHandler = (_event) => {
        _event.preventDefault();
        const opt = "option"+props.quesNum;
        console.log(_event);
        if(props.quesNum+1 < questions.length){
            props.setQuesNum(props.quesNum+1);
            console.log("submit",props.quesNum);
        }
    }



    return(
        <form onSubmit={formHandler}>
            <SectionMedium>
                <div className={classes.container}>{content}</div>
                <button>{currentQues === questions.length-1 ? "Submit" : "Next"}</button>
            </SectionMedium>
        </form>
        );
}

export default Questions;
