import classes from './MobileDrawer.module.css';

const MobileDrawer = () => {
    const menuItems = [
        {
            "id": 1,
            "name": "Home",
            "path": "/",
            "aligment": "left"
        },
        {
            "id": 2,
            "name": "Tasks",
            "path": "/taks",
            "aligment": "left"
        },
        {
            "id": 3,
            "name": "Test",
            "path": "/test",
            "aligment": "left"
        }
    ];

    const menuList = menuItems.filter((menu)=>{
        return <li id={menu.id}>{menu.name}</li>
    });

    const openDrawer = () => {
        document.getElementById("mySidenav").style.width = "100%";
    }

    const closeDrawer = () => {
        document.getElementById("mySidenav").style.width = "0";
    }
    return(
        <div>
            <ul className={classes.navListWrapper} onClick={openDrawer}>
                <li className={`${classes.menuIcon}`}>
                    &#9776;
                </li>
                <li className={`${classes.cedLogoMobile}`}>
                    <img src="/assets/white-logo.png" width="70px" height="55px" alt="logo" />
                </li>
            </ul>
            <div id="mySidenav" className={classes.sidenav}>
                <li className="closebtn" onClick={closeDrawer}>&times;</li>
                {menuList}
            </div>
        </div>
    );
}

export default MobileDrawer;
