import classes from './Form.module.css';
import SectionMedium from '../UI/SectionMedium';
import { useRef } from 'react';

const AddQuestionForm = (props) => {
  const questionRef = useRef();
  const opt1Ref = useRef();
  const opt2Ref = useRef();
  const opt3Ref = useRef();
  const opt4Ref = useRef();

  const questionHandler = (event) => {
    const currentValue = event.target.value;
    return currentValue !== "" && currentValue.length > 0;
  } 

  const submitHandler = (event) => {
    event.preventDefault();
    const question = questionRef.current.value;
    const opt1 = opt1Ref.current.value;
    const opt2 = opt2Ref.current.value;
    const opt3 = opt3Ref.current.value;
    const opt4 = opt4Ref.current.value;

    const options = [];
    if(question.length !== "" && opt1 !== "" && opt2 !== ""){
      options.push(opt1);
      options.push(opt2);
      if(opt3 !== ""){
        options.push(opt3);
      }
      if(opt4 !== ""){
        options.push(opt4);
      }

      const newQuestionData = {
        "qno": Math.random().toFixed(5),
        "question": question,
        "options": options,
      };
      props.addQuestion(newQuestionData);
    }
    questionRef.current.value = "";
    opt1Ref.current.value = "";
    opt2Ref.current.value = "";
    opt3Ref.current.value = "";
    opt4Ref.current.value = "";
  };

  return (
    <SectionMedium>
      <h2 className={classes.h1}>Add question</h2>
      <SectionMedium>
      <form className={classes.form} onSubmit={submitHandler}>
        <div>
          <label>
            Question<br/>
          </label>
          <textarea type='textarea' cols='100' rows='5' name='question' ref={questionRef} placeholder='Enter question along with question mark (?)' onChange={questionHandler}/>
        </div>
        <div>
          <label>
            Option 1<br/>
            <input type='text' name='option1' ref={opt1Ref}/>
          </label>
        </div>
        <div>
          <label>
            Option 2<br/>
            <input type='text' name='option2' ref={opt2Ref}/>
          </label>
        </div>
        <div>
          <label>
            Option 3<br/>
            <input type='text' name='option3' ref={opt3Ref}/>
          </label>
        </div>
        <div>
          <label>
            Option 4<br/>
            <input type='text' name='option4' ref={opt4Ref}/>
          </label>
        </div>
        <button>{props.loading ? 'Adding...' : 'Add Question'}</button>
        <p className="error">{props.errorMsg === "error" ? "Something went wrong. Try again." : ""}</p>
        <p className="success">{props.successMsg === "success" ? "Added successfully." : ""}</p>
      </form>
      </SectionMedium>
    </SectionMedium>
  );
};

export default AddQuestionForm;
